mapper.update:
UPDATE tb_courier
SET pact_id = #{pactId}
WHERE 1 = 1
AND batch_id = #{batchId}

mapper.select:
SELECT a.id , a.name , a.card_id , a.phone , a.bank_card_id , a.deposit_bank , a.join_bank_number , a.entry_date , a.leave_date , a.`status` , a.comment , a.erp_id , a.area , a.site , a.pact_id , b.name AS pact_name , a.city_id , c.name AS city_name , a.creater , a.create_date , a.modify , a.modify_date , a.is_delete FROM tb_courier a LEFT JOIN tb_pact_info b on a.pact_id = b.id LEFT JOIN tb_city_info c on a.city_id = c.id WHERE 1 = 1 AND a.is_delete = 0
AND a.name = #{name}
AND a.card_id = #{cardId}
AND a.phone = #{phone}
AND a.pact_id = #{pactId}

SELECT a.id , a.name , a.card_id , a.phone , a.bank_card_id , a.deposit_bank , a.join_bank_number , a.entry_date , a.leave_date , a.status , a.remark , a.company_id , b.name AS company_name , a.pact_id , c.name AS pact_name , a.erp_id , d.erp_number , a.city_id , e.name AS city_name , a.area_id , f.name AS area_name , a.site_id , g.name AS site_name , a.create_time , a.is_delete FROM tb_courier a LEFT JOIN tb_company b ON a.company_id = b.id LEFT JOIN tb_pact c ON a.pact_id = c.id LEFT JOIN tb_erp d ON a.erp_id = d.id LEFT JOIN tb_city e ON a.city_id = e.id LEFT JOIN tb_area f ON a.area_id = f.id LEFT JOIN tb_site g ON a.site_id = g.id WHERE 1 = 1
AND a.name = #{name}
AND d.erp_number = #{erpNumber}
AND a.card_id = #{cardId}
AND a.company_id = #{companyId}
AND a.status = #{status}
AND a.erp_id = #{pactId}
AND a.city_id = #{cityId}
AND a.area_id = #{areaId}
AND a.site_id = #{siteId}
AND a.is_delete = 0
